const loanForm = document.getElementById('loan-form');

loanForm.addEventListener('submit', function(event) {
  event.preventDefault();

  const loanAmount = document.getElementById('loan-amount').value;
  const loanTerm = document.getElementById('loan-term').value;
  const businessType = document.getElementById('business-type').value;
  const collateral = document.getElementById('collateral').value;

  let interestRate = 0;

  switch(businessType) {
    case 'micro':
      interestRate = 8;
      break;
    case 'small':
      interestRate = 10;
      break;
    case 'medium':
      interestRate = 12;
      break;
  }

  const monthlyInterestRate = interestRate / 1200;
  const monthlyPayment = (loanAmount * monthlyInterestRate) / (1 - Math.pow(1 + monthlyInterestRate, -loanTerm));
  document.getElementById('monthly-payment').value = monthlyPayment.toFixed(2);
});
